mapper.update:
UPDATE ARTIST 
   SET NAME_KOR = #{nameKor}
     , NAME_ENG = #{nameEng}
     , BIRTHDAY = #{birthday}
     , PHONES = #{phones}
     , EMAILS = #{emails}
     , INTRODUCE = #{introduce}
     , HISTORY = #{history}
     , OPEN_YN = #{openYn}
     , EDIT_DATE = NOW()

     , PROFILE = #{profile}

 WHERE ARTIST_NO = #{artistNo}

mapper.select:
SELECT ARTIST_NO, NAME_KOR, NAME_ENG, PROFILE, BIRTHDAY, PHONES, EMAILS, INTRODUCE, HISTORY, OPEN_YN, DELETE_YN,REG_DATE, EDIT_DATE, HIT FROM (SELECT @ROWNUM:=@ROWNUM+1 RNUM, A.ARTIST_NO,NAME_KOR, A.NAME_ENG, A.PROFILE, A.BIRTHDAY, A.PHONES, A.EMAILS, A.INTRODUCE, A.HISTORY, A.OPEN_YN, A.DELETE_YN, A.REG_DATE, A.EDIT_DATE, A.HIT FROM artist A, (SELECT @ROWNUM:=0)R WHERE A.OPEN_YN = 'Y'
AND A.NAME_KOR LIKE CONCAT('%', #{search}, '%')
ORDER BY A.NAME_KOR ASC) LIST WHERE RNUM>= ${start} AND RNUM<=${end}

SELECT  ARTIST_NO, NAME_KOR, NAME_ENG, PROFILE, BIRTHDAY, PHONES, EMAILS, INTRODUCE, HISTORY, OPEN_YN, DELETE_YN,REG_DATE, EDIT_DATE, HIT
FROM artist
WHERE ARTIST_NO=#{artistNo}
