mapper.select:
SELECT * FROM EXHIBITION_VIEW


		TITLE LIKE CONCAT('%',#{title},'%')


		AND START_DATE > #{toDate}


		AND START_DATE < #{endDate}

	AND DELETE_YN = 'N'

mapper.update:
UPDATE exhibition
   SET TITLE = #{title}, CONTENT = #{content}, START_DATE = #{startDate}, OPEN_YN = #{openYn}, SHOW_YN = #{showYn}, EDIT_DATE = NOW()
 WHERE EXHIBITION_NO = #{exhibitionNo}

UPDATE exhibition
   SET MAIN_IMAGE_NAME = #{param1}
 WHERE EXHIBITION_NO = #{param2}

mapper.select:
SELECT EXHIBITION_NO, TITLE, CONTENT, ARTIST_NO, START_DATE, OPEN_YN, SHOW_YN, DELETE_YN, REG_DATE, EDIT_DATE, HIT, ARTIST_NAME_KOR FROM (SELECT @ROWNUM:=@ROWNUM+1 RNUM, E.EXHIBITION_NO, E.TITLE, E.CONTENT, E.ARTIST_NO, E.START_DATE, E.OPEN_YN, E.SHOW_YN, E.DELETE_YN, E.REG_DATE, E.EDIT_DATE, E.HIT, E.ARTIST_NAME_KOR FROM exhibition_view E, (SELECT @ROWNUM:=0)R WHERE E.OPEN_YN = 'Y' AND E.SHOW_YN='Y'
AND E.TITLE LIKE CONCAT('%', #{search}, '%')
ORDER BY E.REG_DATE ASC) LIST WHERE RNUM>= ${start} AND RNUM<=${end}

SELECT EXHIBITION_NO, TITLE, CONTENT, ARTIST_NO, START_DATE, OPEN_YN, SHOW_YN, DELETE_YN, REG_DATE, EDIT_DATE, HIT, ARTIST_NAME_KOR FROM (SELECT @ROWNUM:=@ROWNUM+1 RNUM, E.EXHIBITION_NO, E.TITLE, E.CONTENT, E.ARTIST_NO, E.START_DATE, E.OPEN_YN, E.SHOW_YN, E.DELETE_YN, E.REG_DATE, E.EDIT_DATE, E.HIT, E.ARTIST_NAME_KOR FROM exhibition_view E, (SELECT @ROWNUM:=0)R WHERE E.OPEN_YN = 'N' AND E.SHOW_YN='Y'
AND E.TITLE LIKE CONCAT('%', #{search}, '%')
ORDER BY E.REG_DATE ASC) LIST WHERE RNUM>= ${start} AND RNUM<=${end}

   SELECT T2.ARTWORK_NO, T2.TITLE, T1.ORDER_NO
     FROM exhibition_artwork T1
LEFT JOIN artwork_view T2
       ON T1.ARTWORK_NO = T2.ARTWORK_NO
    WHERE T1.EXHIBITION_NO = #{param1}
 ORDER BY T1.ORDER_NO;

SELECT T1.*
  FROM artwork_view T1
 WHERE T1.ARTIST_NO = #{param2}
   AND T1.ARTWORK_NO NOT IN
       (SELECT T2.ARTWORK_NO FROM exhibition_artwork T2 WHERE T2.EXHIBITION_NO = #{param1});
